document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".publication-box");e.forEach(e=>{const o=e.querySelector(".middle-section"),n=e.getAttribute("data-abstract"),t=o.querySelector(".abstract");let s;const i=6,a=1.5,r=14,c=i*a*r;e.addEventListener("mouseenter",()=>{if(n&&t){clearInterval(s),t.textContent="",o.style.display="flex";let e=0,i="";s=setInterval(()=>{i+=n[e]||"",t.textContent=i,(t.scrollHeight>c||e>=n.length-1)&&(clearInterval(s),e<n.length-1&&(t.textContent=i.trim()+"...")),e++},5)}}),e.addEventListener("mouseleave",()=>{clearInterval(s),t&&(t.textContent=""),o.style.display="none"})})})